import { Component, OnInit} from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface ObjectInfo {
  name: string;
  lastModified: string;
  size: number;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  buckets: string[] = [];
  bucketName: string = '';
  selectedFile: File | null = null;
  objects: ObjectInfo[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.listBuckets();
    this.listObjects();
  }
  listBuckets() {
    this.http.get<{ buckets: string[] }>('http://localhost:8080/listBuckets').subscribe(response => {
      this.buckets = response.buckets;
    });
  }
  createBucket() {
    this.http.post(`http://localhost:8080/createBucket?bucketName=${this.bucketName}`, {}).subscribe(response => {
      console.log(response);
      this.listBuckets();
    });
  }

  deleteBucket() {
    this.http.post(`http://localhost:8080/deleteBucket?bucketName=${this.bucketName}`, {}).subscribe(response => {
      console.log(response);
      this.listBuckets();
    });
  }

  onFileSelected(event: Event) {
    const target = event.target as HTMLInputElement;
    if (target.files && target.files.length > 0) {
      this.selectedFile = target.files[0];
    }
  }

  listObjects() {
    this.http.get<ObjectInfo[]>('http://localhost:8080/list').subscribe(response => {
      this.objects = response;
    });
  }
  uploadFile() {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('file', this.selectedFile);
      this.http.post('http://localhost:8080/upload', formData).subscribe(response => {
        console.log(response);
      });
    }
  }

  downloadFile(objectName: string) {
    this.http.get(`http://localhost:8080/download/${objectName}`, { responseType: 'blob' }).subscribe(blob => {
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = objectName;
      a.click();
    });
  }

  deleteObject(objectName: string) {
    this.http.delete(`http://localhost:8080/delete/${objectName}`).subscribe(response => {
      console.log(response);
      this.listObjects();
    });
  }
}
<h2>Create or Delete Bucket</h2>
<input [(ngModel)]="bucketName" type="text" placeholder="Bucket name">
<button (click)="createBucket()">Create Bucket</button>
<button (click)="deleteBucket()">Delete Bucket</button>

<h2>Buckets</h2>
<ul>
  <li *ngFor="let bucket of buckets">{{ bucket }}</li>
</ul>

<input type="file" (change)="onFileSelected($event)">
<button (click)="uploadFile()">Upload File</button>
<button (click)="downloadFile('example.txt')">Download File</button>

<h2>Objects</h2>
<ul>
  <li *ngFor="let object of objects">
    {{ object.name }} ({{ object.size }} bytes)
    <button (click)="deleteObject(object.name)">Delete</button>
  </li>
</ul>
