import { Component, OnInit  } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../environments/environment';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements {
  title = 'angular';
  buckets: any[] = [];
  newBucketName = '';
  files: any[] = [];
  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.http.get(`${environment.apiUrl}/list`).subscribe((response: any) => {
      this.files = response;
    });
  }

  createBucket(bucketName: string) {
    this.http.post(`${environment.apiUrl}/createBucket`, { bucketName }).subscribe(response => {
      // 处理响应
    });
  }

  deleteBucket(bucketName: string) {
    this.http.post(`${environment.apiUrl}/deleteBucket`, { bucketName }).subscribe(response => {
      // 处理响应
    });
  }

  listBuckets() {
    this.http.get(`${environment.apiUrl}/listBuckets`).subscribe(response => {
      // 处理响应
    });
  }
}
<input [(ngModel)]="newBucketName" />
<button (click)="createBucket(newBucketName)">Create Bucket</button>

<h1>Buckets:</h1>
<ul>
  <li *ngFor="let bucket of buckets">{{ bucket.name }}</li>
</ul>
<ul>
  <li *ngFor="let file of files">
    {{ file.name }} ({{ file.lastModified | date }} - {{ file.size }})
  </li>
</ul>
