import { Component, OnInit} from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface File {
  name: string;
  lastModified: string;
  size: number;
  message: string;
}

interface DeleteBucketResponse {
  message: string;
}
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  buckets: string[] = [];
  bucketName: string = '';
  files: File[] = [];
  selectedBucket: string = '';
  private baseUrl = 'http://localhost:8080'; // Spring Boot 项目的 URL

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.listBuckets();
    this.getFiles();
  }
  formatSize(size: number): string {
    if (size < 1024) {
      return size + ' B';
    } else if (size < 1024 * 1024) {
      return (size / 1024).toFixed(2) + ' KB';
    } else if (size < 1024 * 1024 * 1024) {
      return (size / 1024 / 1024).toFixed(2) + ' MB';
    } else {
      return (size / 1024 / 1024 / 1024).toFixed(2) + ' GB';
    }
  }
  listBuckets() {
    this.http.get<{ buckets: string[] }>('http://localhost:8080/listBuckets').subscribe(response => {
      this.buckets = response.buckets;
    });
  }
  createBucket() {
    this.http.post(`http://localhost:8080/createBucket?bucketName=${this.bucketName}`, {}).subscribe(response => {
      console.log(response);
      this.listBuckets();
    });
  }

  deleteBucket(bucketName: string) {
    this.http.post<DeleteBucketResponse>(`http://localhost:8080/deleteBucket?bucketName=${bucketName}`, null).subscribe(response => {
      if (response.message === '删除成功') {
        this.buckets = this.buckets.filter(bucket => bucket !== bucketName);
      } else {
        // 处理删除失败的情况
      }
    });
  }


  uploadFile(event: any, bucketName: string) {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append('file', file);
    formData.append('bucketName', bucketName);

    this.http.post(`${this.baseUrl}/upload`, formData).subscribe(
      data => {
        console.log(data);
        this.getFiles();
      },
      error => {
        console.log(error);
      }
    );
  }

  getFiles() {
    if (this.selectedBucket) {
      this.http.get<File[]>(`${this.baseUrl}/list?bucketName=${this.selectedBucket}`).subscribe(
        data => {
          this.files = data;
        },
        error => {
          console.log(error);
        }
      );
    }
  }

  downloadFile(name: string) {
    this.http.get(`${this.baseUrl}/download/${name}?bucketName=${this.selectedBucket}`, { responseType: 'blob' }).subscribe(
      data => {
        const url = window.URL.createObjectURL(data);
        const link = document.createElement('a');
        link.href = url;
        link.download = name;
        link.click();
      },
      error => {
        console.log(error);
      }
    );
  }

  deleteFile(name: string) {
    this.http.delete(`${this.baseUrl}/delete/${name}?bucketName=${this.selectedBucket}`, { responseType: 'text' }).subscribe(
      data => {
        console.log(data);
        this.getFiles(); // 重新获取文件列表
      },
      error => {
        console.log(error);
      }
    );
  }

}

<h2>Buckets</h2>
<ul>
  <div class="group">
    <input [(ngModel)]="bucketName" type="text" placeholder="Bucket name">
    <button (click)="createBucket()">Create</button></div>
  <table>
    <tr>
      <th>Delete</th>
      <th>Bucket</th>
      <th>Upload File</th>
    </tr>
    <tr *ngFor="let bucket of buckets">
      <td><button class="delete-button" (click)="deleteBucket(bucket)"> X </button></td>
      <td>{{ bucket }}</td>
      <td><input type="file" (change)="uploadFile($event, bucket)" /></td>
    </tr>
  </table>
</ul>

<h2>Objects</h2>
<h3>Choose bucket:
<select [(ngModel)]="selectedBucket" (change)="getFiles()">
  <option *ngFor="let bucket of buckets" [value]="bucket">{{ bucket }}</option>
</select></h3>
<table>
  <tr>
    <th>Name</th>
    <th>Last Modified</th>
    <th>Size</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let file of files">
    <td>{{ file.name }}</td>
    <td>{{ file.lastModified }}</td>
    <td>{{ formatSize(file.size) }}</td>
    <td>
    <span class="action-buttons">
        <button class="download-button" (click)="downloadFile(file.name)">Download</button>
        /
        <button class="delete-button1" (click)="deleteFile(file.name)">Delete</button>
    </span>
    </td>
  </tr>
</table>
