import { Component, OnInit} from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface ObjectInfo {
  name: string;
  lastModified: string;
  size: number;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  buckets: string[] = [];
  bucketName: string = '';
  selectedFile: File | null = null;
  objects: ObjectInfo[] = [];
  private baseUrl = 'http://localhost:8080'; // Spring Boot 项目的 URL

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.listBuckets();
    this.getFiles();
  }
  listBuckets() {
    this.http.get<{ buckets: string[] }>('http://localhost:8080/listBuckets').subscribe(response => {
      this.buckets = response.buckets;
    });
  }
  createBucket() {
    this.http.post(`http://localhost:8080/createBucket?bucketName=${this.bucketName}`, {}).subscribe(response => {
      console.log(response);
      this.listBuckets();
    });
  }

  deleteBucket() {
    this.http.post(`http://localhost:8080/deleteBucket?bucketName=${this.bucketName}`, {}).subscribe(response => {
      console.log(response);
      this.listBuckets();
    });
  }

  onFileSelected(event: Event) {
    const target = event.target as HTMLInputElement;
    if (target.files && target.files.length > 0) {
      this.selectedFile = target.files[0];
    }
  }

  getFiles() {
    this.http.get(`${this.baseUrl}/list`).subscribe(
      data => {
        this.files = data;
      },
      error => {
        console.log(error);
      }
    );
  }
  uploadFile() {
    if (this.selectedFile) {
      const formData = new FormData();
      formData.append('file', this.selectedFile);
      this.http.post('http://localhost:8080/upload', formData).subscribe(response => {
        console.log(response);
      });
    }
  }

  downloadFile(name: string) {
    this.http.get(`${this.baseUrl}/download/${name}`, { responseType: 'blob' }).subscribe(
      data => {
        const url = window.URL.createObjectURL(data);
        const link = document.createElement('a');
        link.href = url;
        link.download = name;
        link.click();
      },
      error => {
        console.log(error);
      }
    );
  }

  deleteFile(name: string) {
    this.http.delete(`${this.baseUrl}/delete/${name}`).subscribe(
      data => {
        console.log(data);
        this.getFiles();
      },
      error => {
        console.log(error);
      }
    );
  }

}
<h2>Create or Delete Bucket</h2>
<input [(ngModel)]="bucketName" type="text" placeholder="Bucket name">
<button (click)="createBucket()">Create Bucket</button>
<button (click)="deleteBucket()">Delete Bucket</button>

<h2>Buckets</h2>
<ul>
  <li *ngFor="let bucket of buckets">{{ bucket }}</li>
</ul>


<h2>Objects</h2>
<ul>
  <li *ngFor="let file of files">
    {{ file.name }} ({{ file.lastModified }} / {{ file.size }})
    <button (click)="downloadFile(file.name)">Download</button>
    <button (click)="deleteFile(file.name)">Delete</button>
  </li>
</ul>
