import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  buckets: string[] = [];
  constructor(private http: HttpClient) {}

  listBuckets() {
    this.http.get<{ buckets: string[] }>('http://localhost:8080/listBuckets').subscribe(response => {
      this.buckets = response.buckets;
    });
  }
  createBucket(bucketName: string) {
    this.http.post(`http://localhost:8080/createBucket?bucketName=${bucketName}`, {}).subscribe(response => {
      console.log(response);
    });
  }

  deleteBucket(bucketName: string) {
    this.http.post(`http://localhost:8080/deleteBucket?bucketName=${bucketName}`, {}).subscribe(response => {
      console.log(response);
    });
  }
}
<ul>
  <li *ngFor="let bucket of buckets">{{ bucket }}</li>
</ul>
<input #bucketNameInput type="text" placeholder="Bucket name">
<button (click)="createBucket(bucketNameInput.value)">Create Bucket</button>
<button (click)="deleteBucket(bucketNameInput.value)">Delete Bucket</button>
