import { Component } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'angular';
  buckets: any[] = [];
  newBucketName = '';
  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.getBuckets();
  }

  getBuckets() {
    this.http.get('/listBuckets').subscribe((data: any) => {
      this.buckets = data;
    });
  }
  createBucket(bucketName: string) {
    this.http.post('/createBucket', { bucketName }).subscribe(data => {
      // 处理返回的数据
    });
  }
}
<input [(ngModel)]="newBucketName" />
<button (click)="createBucket(newBucketName)">Create Bucket</button>

<ul>
  <li *ngFor="let bucket of buckets">{{ bucket.name }}</li>
</ul>
