import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { environment } from '../environments/environment';


@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'angular';
  buckets: any[] = [];
  newBucketName = '';
  constructor(private http: HttpClient) {}

  createBucket(bucketName: string) {
    this.http.post('${environment.apiUrl}/createBucket', { bucketName }).subscribe(response => {
      // 处理响应
    });
  }

  deleteBucket(bucketName: string) {
    this.http.post('${environment.apiUrl}/deleteBucket', { bucketName }).subscribe(response => {
      // 处理响应
    });
  }

  listBuckets() {
    this.http.get('${environment.apiUrl/listBuckets}').subscribe(response => {
      // 处理响应
    });
  }
}
<input [(ngModel)]="newBucketName" />
<button (click)="createBucket(newBucketName)">Create Bucket</button>

<h1>Buckets:</h1>
<ul>
  <li *ngFor="let bucket of buckets">{{ bucket.name }}</li>
</ul>
